fieldValues.fieldValue:
={{
  (() => {
    // 1. Tomar el campo y asegurarse de que es string
    let p = $json['phone number'].toString().trim();

    // 2. Quitar '+' al inicio (si existe)
    if (p.startsWith('+')) {
      p = p.slice(1);
    }

    // 3. Eliminar todo lo que no sea dígito
    p = p.replace(/\D/g, '');

    // 4. Si quedan 10 dígitos, anteponer '57'
    if (p.length === 10) {
      return `57${p}`;
    }

    // 5. En cualquier otro caso (más de 10 dígitos), devolver tal cual
    return p;
  })()
}}

