parameters.jsCode:
// 1. Leer y convertir a string
const rawPrimary = $('Form').first().json['Your Phone Number'].toString().trim();
const rawCouple  = $('Form').first().json['Your Couple Number'].toString().trim();

// 2. Función de normalización (igual a la que ya tienes)
function normalize(p) {
  let str = p.startsWith("+") ? p.slice(1) : p;
  str = str.replace(/\D/g, "");
  return str.length === 10 ? `57${str}` : str;
}

// 3. Obtener valores limpios
const primary = normalize(rawPrimary);
const couple  = normalize(rawCouple);

// 4. Devolver dos items — n8n iterará uno a uno
return [
  { json: { phone_number: primary } },
  { json: { phone_number: couple  } },
];

