}

:root {
  /* ===== Brand ===== */
  --brand-primary: #6E37E4; /* morado */
  --brand-accent:  #13D3E5; /* cian */

  /* ===== Tipografía ===== */
  --font-family: 'Open Sans', sans-serif;
  --font-weight-normal: 400;
  --font-weight-bold: 600;
  --font-size-body: 12px;
  --font-size-label: 14px;
  --font-size-test-notice: 12px;
  --font-size-input: 14px;
  --font-size-header: 20px;
  --font-size-paragraph: 14px;
  --font-size-link: 12px;
  --font-size-error: 12px;
  --font-size-html-h1: 28px;
  --font-size-html-h2: 20px;
  --font-size-html-h3: 16px;
  --font-size-html-h4: 14px;
  --font-size-html-h5: 12px;
  --font-size-html-h6: 10px;
  --font-size-subheader: 14px;

  /* ===== Colores ===== */
  --color-background: #fbfcfe;
  --color-card-bg: #ffffff;
  --color-card-border: #dbdfe7;
  --color-card-shadow: rgba(110, 55, 228, 0.10); /* sombreado con tu primario */

  --color-header: #2f1f6a;          /* primario oscurecido p/ títulos */
  --color-header-subtext: #7e8186;  /* gris legible */
  --color-html-text: #555;

  --color-link: var(--brand-primary);
  --color-html-link: var(--brand-primary);

  --color-input-border: #dbdfe7;
  --color-input-text: #71747A;
  --color-focus-border: var(--brand-primary);

  --color-submit-btn-bg: var(--brand-primary);
  --color-submit-btn-text: #ffffff;
  /* hover/focus (fijo para emails que no soportan :hover dinámico) */
  --color-submit-btn-bg-hover: #5a2fd0; /* primario ~12% más oscuro */

  --color-clear-button-bg: var(--brand-accent);

  --color-required: var(--brand-primary);
  --color-error: #ea1f30; /* conserva rojo para estados de error (semántica) */

  /* Avisos (mapeados a tu acento; si prefieres “warning” ámbar, ver nota abajo) */
  --color-test-notice-text: #0b6e78; /* cian oscuro legible */
  --color-test-notice-bg:   #e6fbfe; /* cian muy claro */
  --color-test-notice-border: #a7edf5;

  /* ===== Radios ===== */
  --border-radius-card: 8px;
  --border-radius-input: 6px;
  --border-radius-clear-btn: 50%;
  --card-border-radius: 8px;

  /* ===== Espaciado ===== */
  --padding-container-top: 24px;
  --padding-card: 24px;
  --padding-test-notice-vertical: 12px;
  --padding-test-notice-horizontal: 24px;
  --margin-bottom-card: 16px;
  --padding-form-input: 12px;
  --card-padding: 24px;
  --card-margin-bottom: 16px;

  /* ===== Dimensiones ===== */
  --container-width: 448px;
  --submit-btn-height: 48px;
  --checkbox-size: 18px;

  /* ===== Otros ===== */
  --box-shadow-card: 0px 4px 16px 0px var(--color-card-shadow);
  --opacity-placeholder: 0.5;
}
